/**
 * Class for the PaSoRich - PaSoRi with Scratch 3.0
 * @param {Runtime} runtime - the runtime instantiating this block package.
 * @constructor
 */
class Scratch3Pasorich {
    constructor (runtime) {
        /**
         * The runtime instantiating this block package.
         * @type {Runtime}
         */
        this.runtime = runtime;

//        console.log("initializing...");
        if (pasoriDevice != null) {
            pasoriDevice.close();
            pasoriDevice = null;
        }
        navigator.usb.requestDevice({ filters: [{ vendorId: 0x054c }] })
        .then(selectedDevice => {
            pasoriDevice = selectedDevice;
            return pasoriDevice.open();
        })
        .then(() => 
            pasoriDevice.selectConfiguration(1)
        )
        .then(() => 
            pasoriDevice.claimInterface(0)
        );
//        console.log("init_done");
    }


    /**
     * @returns {object} metadata for this extension and its blocks.
     */
    getInfo () {
        return {
            id: 'pasorich',
            name: 'PaSoRich',
            menuIconURI: menuIconURI,
            blockIconURI: blockIconURI,
            blocks: [
                {
                    opcode: 'readPasori',
                    text: 'read PaSoRi',
                    blockType: BlockType.COMMAND,
                    text: 'read PaSoRi'
                },
                {
                    opcode: 'getIdm',
                    text: 'Idm',
                    blockType: BlockType.REPORTER
                },
                {
                    opcode: 'getReadingFlag',
                    text: 'reading',
                    blockType: BlockType.BOOLEAN
                },
                {
                    opcode: 'openPasori',
                    text: 'open PaSoRi',
                    blockType: BlockType.COMMAND,
                    text: 'open PaSoRi'
                },
                {
                    opcode: 'closePasori',
                    text: 'close PaSoRi',
                    blockType: BlockType.COMMAND,
                    text: 'close PaSoRi'
                }
            ],
            menus: {
            }
        };
    }




    readPasori () {
//        console.log('=== S:readPaSoRi ===');

        if(readingFlag){return;}
        readingFlag = true;

        if(pasoriDevice.opened){
            pasoriDevice.close();
        }

        navigator.usb.getDevices().then(devices => {
            console.log(devices);
            devices.map(selectedDevice => {
                pasoriDevice = selectedDevice;
                pasoriDevice.open()
                .then(() => 
                    pasoriDevice.selectConfiguration(1)
                )
                .then(() => 
                    pasoriDevice.claimInterface(0)
                );
            });
            session(pasoriDevice);
        })
        .catch(error => { console.log(error); });

//        console.log('=== E:readPaSoRi ===');
    }



    getIdm () {
        return idnum;
    }

    getReadingFlag () {
        return readingFlag;
    }


    openPasori () {
//        console.log('=== S:openPaSoRi ===');
        
        if(readingFlag){return;}

        if (pasoriDevice != null) {
            pasoriDevice.close();
            pasoriDevice = null;
        }

        navigator.usb.getDevices().then(devices => {
            console.log(devices);
            devices.map(selectedDevice => {
                pasoriDevice = selectedDevice;
                pasoriDevice.open()
                .then(() => 
                    pasoriDevice.selectConfiguration(1)
                )
                .then(() => 
                    pasoriDevice.claimInterface(0)
                );
            });
        })
        .catch(error => { console.log(error); });

//        console.log('=== E:openPaSoRi ===');
    }


    closePasori () {
        if(readingFlag){return;}
        return pasoriDevice.close();
    }


}
module.exports = Scratch3Pasorich;